mapper.delete:
delete
from NOTICE
where BOARDNO = #{boardno}
and BOARDINFONO = #{boardinfono}

mapper.select:
select

from NOTICE
where BOARDNO = #{boardno}
and BOARDINFONO = #{boardinfono}

select

from NOTICE
where BOARDINFONO = '1'
and BOARDNO in (select BOARDNO from NOTICEADMISSIONBOX where STAT = 'A' and NOTICE.BOARDINFONO = '1')
order by BOARDNO ${sortBy}
limit ${offset}, ${limit}
